$(".view_message").click(function(){var b=JSON.parse(($(this).attr("data")));bootbox.dialog({message:b.message,title:"Message",buttons:{danger:{label:"Close"}}});if(b.read==false){$.ajax({url:_PAGE_URL+"action/message_read",type:"POST",data:{id:b.id},success:function(a){if(a==true){$(".unread"+b.id).html("Read").removeClass("label-grey").addClass("label-lightgrey");$("li.msg_id"+b.id).remove();if($(".msg_count_h").html()==1){$(".msg_count_li").html("No unread messages")}else{if($(".msg_count_h").html()>1){$(".msg_count_li").html(($(".msg_count_h").html()-1)+" unread messages")}}if($(".msg_count_h").html()!=0){$(".msg_count_h").html($(".msg_count_h").html()-1)}}}})}});$(".accept, .deny").click(function(){var d=$(this).attr("class");var c=$(this).attr("id");$.ajax({url:_PAGE_URL+"action/friend",type:"POST",data:{id:c,action:d},success:function(b){b=JSON.parse(b);$.gritter.add({title:b.title,text:b.text,class_name:"gritter-"+b.type+(!$("#gritter-light").get(0)?" gritter-light":"")});if(b.type==="success"){$("a.accept").remove();$("a.deny").remove();var a=$("div.member"+c);a.fadeOut(1000,function(){a.remove()})}}})});$(".add_friend").click(function(){var b=$(this).attr("id");$.ajax({url:_PAGE_URL+"action/add_friend",type:"POST",data:{id:b},success:function(a){a=JSON.parse(a);$.gritter.add({title:a.title,text:a.text,class_name:"gritter-"+a.type+(!$("#gritter-light").get(0)?" gritter-light":"")})}})});$(".send_message").click(function(){var c=($(this).attr("id"));var d=($(this).attr("name"));bootbox.dialog({message:'<form name="message_form" id="message_form" action="" method="post">						<div class="form_group">							<label class="col-sm-3 control-label no-padding-right"> Message </label>							<textarea class="form-control limited" maxlength="255" name="message" style="max-width:400px;max-height:150px;min-height:150px;" />						</div>					</form>',title:"Send message to <b>"+d,buttons:{success:{label:"Send",className:"btn-success",callback:function(b){var a=$("#message_form");data=a.serialize();$.ajax({url:_PAGE_URL+"action/message_send",type:"POST",data:{id:c,data:data},success:function(f){f=JSON.parse(f);$.gritter.add({title:f.title,text:f.text,class_name:"gritter-"+f.type+(!$("#gritter-light").get(0)?" gritter-light":"")});if(f.type==="success"){bootbox.hideAll()}}});return false}},danger:{label:"Cancel"}}})});